import{_ as t,o as l,c as o,f as e,a as r}from"./chunks/framework.BkI9ovSD.js";const s="/assets/b_bo_WAhABlgIQAYRECc!.CBhHj1C4.jpeg",u=JSON.parse('{"title":"樱花节后","description":"","frontmatter":{"title":"樱花节后","date":"2021-03-19T00:00:00.000Z","categories":["Life"]},"headers":[],"relativePath":"blog/Life/樱花节后.md","filePath":"blog/Life/樱花节后.md","lastUpdated":1734488008000}'),n={name:"blog/Life/樱花节后.md"};function i(d,a,p,c,_,f){return l(),o("div",{"data-pagefind-body":!0},a[0]||(a[0]=[e("h1",{id:"樱花节后",tabindex:"-1"},[r("樱花节后 "),e("a",{class:"header-anchor",href:"#樱花节后","aria-label":'Permalink to "樱花节后"'},"​")],-1),e("p",null,[e("img",{src:s,alt:"img"})],-1),e("p",null,"三月中旬，樱花节落下帷幕，花瓣在细如发丝的春雨中落满草坪，通往教三的林间小路也因此增添了一抹粉色。但和赏花的熙攘人群一同离去的，还有黄家湖这几日的短暂暖阳。",-1),e("p",null,"春分未至，寒风像张牙舞爪的怪兽，肆意地穿过教室三楼的窗缝，在每个人耳边发出呼啸的鸣叫。",-1),e("p",null,"春寒料峭的日子里，我收到了朋友寄来的明信片，来自远方的几许笔墨，读来却蕴含着入木三分的珍重。“春信今年早，江头昨夜寒”，翻山越岭而来的礼物，使人在阴霾的天气里忘却了寒冷，带来的却是星城满满的阳光和新鲜。",-1),e("p",null,"从一堆泛黄的纸张旧物里，散落出了几片玉兰花瓣，那大概是高中同学在某年三月夹在信里的附赠。时过境迁，人生各自飘零，过去的信件再难以找到，内容我也早已忘的一干二净，但那些细碎的玉兰花瓣，却成为了记忆里永恒的斑驳，嵌在了故乡的绿瓦红砖里。",-1),e("p",null,"傍晚，室内的灯光亮起，窗外变得雾蒙蒙的，眼前的水杯里不再翻腾热气，旁边的哥们也冻的直流鼻涕。我终于放弃了对气温回暖的幻想，下定决心要从衣柜里翻出几条厚实的秋裤，加入这个春季的全新穿搭。",-1)]))}const g=t(n,[["render",i]]);export{u as __pageData,g as default};
