import{_ as t,o as l,c as s,f as e,a as r}from"./chunks/framework.BkI9ovSD.js";const n="/assets/dsn.2ELuB9wL.jpeg",u=JSON.parse('{"title":"第三年","description":"","frontmatter":{"title":"第三年","date":"2022-01-02T00:00:00.000Z","categories":["Life"]},"headers":[],"relativePath":"blog/Life/第三年.md","filePath":"blog/Life/第三年.md","lastUpdated":1734486687000}'),o={name:"blog/Life/第三年.md"};function i(d,a,p,c,f,_){return l(),s("div",{"data-pagefind-body":!0},a[0]||(a[0]=[e("h1",{id:"第三年",tabindex:"-1"},[r("第三年 "),e("a",{class:"header-anchor",href:"#第三年","aria-label":'Permalink to "第三年"'},"​")],-1),e("p",null,[e("img",{src:n,alt:"img"})],-1),e("p",null,"不知不觉，我们居然已经是和新冠病毒共处的第三年了，和朋友相约在人民医院打完第三针疫苗，学校也终于赶在元旦前给所有人放了长假。",-1),e("p",null,"清晨的太阳缓缓爬上天际线，气温回升，沁湖的空气比起前几天要温润了许多，寒风也不再那么凛冽而霸道，非要一个劲地往人的袖口里钻。",-1),e("p",null,"走出校门，我才发现武汉的地铁居然已经通到了黄家湖校区，而原本拥挤逼仄的34路公交这次在眼前空荡荡地驶过，我只好在青菱街道的拐角路口与它挥手告别。",-1),e("p",null,"时隔半年，终于又回到了自己的家，庆幸于可以安心地睡到自然醒。在半梦半醒间，听到母亲在厨房切菜的声音，从没发现竟是如此的悦耳动听，我这才开始体会到汪曾祺在《冬天》里描述的场景，所谓“家人闲坐，灯火可亲”。",-1),e("p",null,"紧绷的日子还未结束，但在熟悉的小镇里，人的心境还是相对悠然一些。路过十点半还在营业的包子铺，眼前的长街在落日的余晖里显得有些复古。那些傍晚时分弥漫在街头巷尾的炊烟味道，像是梵高古画里残留的墨迹，挑逗着每一个归乡者的隐秘记忆。",-1),e("p",null,"元旦已过，新年伊始。对于过去我心存感激，对于未来我也保持憧憬。来日方长，祝福这座城，也祝福我遇见的所有人。",-1)]))}const g=t(o,[["render",i]]);export{u as __pageData,g as default};
